<?php/** * This is the model class for table "{{producer_account}}". * * The followings are the available columns in table '{{producer_account}}': * @property integer $Pro_Acc_Id * @property string $Pro_Corporate_Name * @property string $Pro_Internal_Code * @property integer $Pro_Ipi * @property integer $Pro_Ipi_Base_Number * @property string $Pro_Date * @property string $Pro_Place * @property integer $Pro_Country_Id * @property integer $Pro_Legal_Form_id * @property string $Pro_Reg_Date * @property string $Pro_Reg_Number * @property string $Pro_Excerpt_Date * @property integer $Pro_Language_Id * @property string $Pro_Non_Member * @property string $Pro_Is_Publisher * @property string $Active * @property string $Created_Date * @property string $Rowversion * * The followings are the available model relations: * @property MasterCountry $proCountry * @property MasterLanguage $proLanguage * @property MasterLegalForm $proLegalForm * @property ProducerAccountAddress[] $producerAccountAddresses * @property ProducerBiography[] $producerBiographies * @property ProducerPaymentMethod[] $producerPaymentMethods * @property ProducerPseudonym[] $producerPseudonyms * @property ProducerRelatedRights[] $producerRelatedRights * @property ProducerSuccession[] $producerSuccessions */class ProducerAccount extends CActiveRecord {    public $search_status;    public $is_producer;    public $after_save_disable = true;    public $after_delete_disable = true;    public function init() {        parent::init();        if ($this->isNewRecord) {            $this->Pro_Country_Id = DEFAULT_COUNTRY_ID;            $this->Pro_Language_Id = DEFAULT_LANGUAGE_ID;        }    }    /**     * @return string the associated database table name     */    public function tableName() {        return '{{producer_account}}';    }    public function scopes() {        $alias = $this->getTableAlias(false, false);        return array(            'isActive' => array('condition' => "$alias.Active = '1'"),            'isStatusActive' => array('condition' => "$alias.Pro_Non_Member = 'N' AND (producerRelatedRights.Pro_Rel_Exit_Date is Null OR producerRelatedRights.Pro_Rel_Exit_Date = '0000-00-00' OR producerRelatedRights.Pro_Rel_Exit_Date >= DATE(NOW()))")//            'isStatusActive' => array('condition' => "producerRelatedRights.Pro_Rel_Exit_Date is not Null And producerRelatedRights.Pro_Rel_Exit_Date != '0000-00-00' And producerRelatedRights.Pro_Rel_Exit_Date >= DATE(NOW())")        );    }    /**     * @return array validation rules for model attributes.     */    public function rules() {        // NOTE: you should only define rules for those attributes that        // will receive user inputs.        return array(            array('Pro_Corporate_Name, Pro_Internal_Code, Pro_Date', 'required'),            array('Pro_Ipi, Pro_Ipi_Base_Number, Pro_Country_Id, Pro_Legal_Form_id, Pro_Language_Id', 'numerical', 'integerOnly' => true),            array('Pro_Corporate_Name, Pro_Internal_Code, Pro_Place, Pro_Reg_Number', 'length', 'max' => 255),            array('Active', 'length', 'max' => 1),            array('Pro_Internal_Code, Pro_Corporate_Name', 'unique'),            array('Pro_Reg_Date, Pro_Excerpt_Date, Created_Date, Rowversion, Pro_Non_Member, Pro_Is_Publisher, is_producer', 'safe'),            // The following rule is used by search().            // @todo Please remove those attributes that should not be searched.            array('Pro_Acc_Id, Pro_Corporate_Name, Pro_Internal_Code, Pro_Ipi, Pro_Ipi_Base_Number, Pro_Date, Pro_Place, Pro_Country_Id, Pro_Legal_Form_id, Pro_Reg_Date, Pro_Reg_Number, Pro_Excerpt_Date, Pro_Language_Id, Active, Created_Date, Rowversion', 'safe', 'on' => 'search'),        );    }    /**     * @return array relational rules.     */    public function relations() {        // NOTE: you may need to adjust the relation name and the related        // class name for the relations automatically generated below.        return array(            'proCountry' => array(self::BELONGS_TO, 'MasterCountry', 'Pro_Country_Id'),            'proLanguage' => array(self::BELONGS_TO, 'MasterLanguage', 'Pro_Language_Id'),            'proLegalForm' => array(self::BELONGS_TO, 'MasterLegalForm', 'Pro_Legal_Form_id'),            'producerAccountAddresses' => array(self::HAS_ONE, 'ProducerAccountAddress', 'Pro_Acc_Id'),            'producerBiographies' => array(self::HAS_ONE, 'ProducerBiography', 'Pro_Acc_Id'),            'producerPaymentMethods' => array(self::HAS_ONE, 'ProducerPaymentMethod', 'Pro_Acc_Id'),            'producerPseudonyms' => array(self::HAS_ONE, 'ProducerPseudonym', 'Pro_Acc_Id'),            'producerRelatedRights' => array(self::HAS_ONE, 'ProducerRelatedRights', 'Pro_Acc_Id'),            'producerSuccessions' => array(self::HAS_ONE, 'ProducerSuccession', 'Pro_Acc_Id'),            'producerGroupMembers' => array(self::HAS_MANY, 'PublisherGroupMembers', 'Pub_Group_Member_Internal_Code',                'foreignKey' => array('Pub_Group_Member_Internal_Code' => 'Pro_Internal_Code')            ),        );    }    /**     * @return array customized attribute labels (name=>label)     */    public function attributeLabels() {        return array(            'Pro_Acc_Id' => 'Id',            'Pro_Corporate_Name' => 'Corporate Name',            'Pro_Internal_Code' => 'Internal Code',            'Pro_Ipi' => 'IPI Name Number',            'Pro_Ipi_Base_Number' => 'IPI Base Number',            'Pro_Date' => 'Date of Foundation',            'Pro_Place' => 'Place',            'Pro_Country_Id' => 'Country',            'Pro_Legal_Form_id' => 'Legal Form',            'Pro_Reg_Date' => 'Registration Date',            'Pro_Reg_Number' => 'Registration Number',            'Pro_Excerpt_Date' => 'Excerpt Date',            'Pro_Language_Id' => 'Language',            'Active' => 'Active',            'Created_Date' => 'Created Date',            'Rowversion' => 'Rowversion',            'search_status' => 'Status',            'Pro_Non_Member' => 'Non Member',            'Pro_Is_Publisher' => 'Publisher',            'is_producer' => 'Producer',        );    }    /**     * Retrieves a list of models based on the current search/filter conditions.     *     * Typical usecase:     * - Initialize the model fields with values from filter form.     * - Execute this method to get CActiveDataProvider instance which will filter     * models according to data in model fields.     * - Pass data provider to CGridView, CListView or any similar widget.     *     * @return CActiveDataProvider the data provider that can return the models     * based on the search/filter conditions.     */    public function search() {        // @todo Please modify the following code to remove attributes that should not be searched.        $criteria = new CDbCriteria;        $criteria->with = array('producerRelatedRights');        $criteria->compare('Pro_Acc_Id', $this->Pro_Acc_Id);        $criteria->compare('Pro_Corporate_Name', $this->Pro_Corporate_Name, true);        $criteria->compare('Pro_Internal_Code', $this->Pro_Internal_Code, true);        $criteria->compare('Pro_Ipi', $this->Pro_Ipi);        $criteria->compare('Pro_Ipi_Base_Number', $this->Pro_Ipi_Base_Number);        $criteria->compare('Pro_Date', $this->Pro_Date, true);        $criteria->compare('Pro_Place', $this->Pro_Place, true);        $criteria->compare('Pro_Country_Id', $this->Pro_Country_Id);        $criteria->compare('Pro_Legal_Form_id', $this->Pro_Legal_Form_id);        $criteria->compare('Pro_Reg_Date', $this->Pro_Reg_Date, true);        $criteria->compare('Pro_Reg_Number', $this->Pro_Reg_Number, true);        $criteria->compare('Pro_Excerpt_Date', $this->Pro_Excerpt_Date, true);        $criteria->compare('Pro_Language_Id', $this->Pro_Language_Id);        $criteria->compare('Active', $this->Active, true);        $criteria->compare('Created_Date', $this->Created_Date, true);        $criteria->compare('Rowversion', $this->Rowversion, true);        $now = new CDbExpression("DATE(NOW())");        if ($this->search_status == 'A') {            $criteria->addCondition('producerRelatedRights.Pro_Rel_Exit_Date >= ' . $now . ' OR producerRelatedRights.Pro_Rel_Exit_Date = "0000-00-00" OR producerRelatedRights.Pro_Rel_Exit_Date is null');            $criteria->compare('Pro_Non_Member', 'N', true);        } elseif ($this->search_status == 'I') {            $criteria->compare('Pro_Non_Member', 'Y', true);        } elseif ($this->search_status == 'E') {            $criteria->addCondition('producerRelatedRights.Pro_Rel_Exit_Date < ' . $now . ' And producerRelatedRights.Pro_Rel_Exit_Date != "0000-00-00"');            $criteria->compare('Pro_Non_Member', 'N', true);        }        return new CActiveDataProvider($this, array(            'criteria' => $criteria,            'pagination' => false//            'pagination' => array(//                'pageSize' => PAGE_SIZE,//            )        ));    }    /**     * Returns the static model of the specified AR class.     * Please note that you should have this exact method in all your CActiveRecord descendants!     * @param string $className active record class name.     * @return ProducerAccount the static model class     */    public static function model($className = __CLASS__) {        return parent::model($className);    }    public function dataProvider() {        return new CActiveDataProvider($this, array(            'pagination' => false//            'pagination' => array(//                'pageSize' => PAGE_SIZE,//            )        ));    }    protected function afterSave() {        if ($this->isNewRecord) {            $gen_inter_model = InternalcodeGenerate::model()->find("Gen_User_Type = :type", array(':type' => 'PR'));            $len = strlen($gen_inter_model->Gen_Inter_Code);            $gen_inter_model->Gen_Inter_Code = str_pad(($gen_inter_model->Gen_Inter_Code + 1), $len, "0", STR_PAD_LEFT);            $gen_inter_model->save(false);            if ($this->Pro_Is_Publisher == 'Y') {                $this->convert($this->Pro_Acc_Id);            }        } elseif ($this->after_save_disable && !$this->isNewRecord) {            $publisher_model = $this->checkPublisher($this->Pro_Internal_Code, false);            switch ($this->Pro_Is_Publisher) {                case 'N':                    if (!empty($publisher_model)) {                        $publisher_model->after_delete_disable = false;                        $publisher_model->delete();                    }                    break;                case 'Y':                    $this->convert($this->Pro_Acc_Id);                    break;            }        }        return parent::afterSave();    }    public function getStatus() {        if ($this->Pro_Non_Member == 'Y') {            $status = '<i class="fa fa-circle text-red" title="Non-member"></i>';        } else {            $status = '<i class="fa fa-circle text-green" title="Active"></i>';            if ($this->producerRelatedRights && $this->producerRelatedRights->Pro_Rel_Exit_Date != '' && $this->producerRelatedRights->Pro_Rel_Exit_Date != '0000-00-00') {                if (strtotime($this->producerRelatedRights->Pro_Rel_Exit_Date) < strtotime(date('Y-m-d'))) {                    $status = '<i class="fa fa-circle text-yellow" title="Expired"></i>';                }            }        }        return $status;    }    public function checkPublisher($internal_code, $ret_sts = true) {        $publisher = PublisherAccount::model()->findByAttributes(array('Pub_Internal_Code' => $internal_code));        if ($ret_sts)            return !empty($publisher);        return $publisher;    }    protected function afterDelete() {        $publisher_model = $this->checkPublisher($this->Pro_Internal_Code, false);        if (!empty($publisher_model) && $this->after_delete_disable) {            $publisher_model->after_delete_disable = false;            $publisher_model->delete();        }        return parent::afterDelete();    }    public function convert($id) {        $producer_model = self::model()->findByPk($id);        $check_exists = $this->checkPublisher($producer_model->Pro_Internal_Code, false);        $ignore_list = Myclass::getPublisherconvertIgnorelist();        $publisher_model = empty($check_exists) ? new PublisherAccount : $check_exists;        //basic data        foreach ($producer_model->attributes as $key => $value) {            $attr_name = str_replace('Pro_', 'Pub_', $key);            !in_array($key, $ignore_list) ? $publisher_model->setAttribute($attr_name, $value) : '';        }        $publisher_model->Pub_Is_Producer = 'Y';        $publisher_model->after_save_disable = false;        $publisher_model->save(false);        $auth_acc_id = $publisher_model->Pub_Acc_Id;        if (!$this->isNewRecord) {            $relModels = array(                'producerAccountAddresses' => 'PublisherAccountAddress',                'producerPaymentMethods' => 'PublisherPaymentMethod',                'producerBiographies' => 'PublisherBiography',                'producerPseudonyms' => 'PublisherPseudonym',                'producerDeathInheritances' => 'PublisherDeathInheritance',            );            foreach ($relModels as $k => $v) {                if (!empty($producer_model->$k)) {                    $auth_rel_model = new $v;                    $auth_rel_model->Pub_Acc_Id = $auth_acc_id;                    foreach ($producer_model->$k->attributes as $key => $value) {                        $attr_name = str_replace('Pro_', 'Pub_', $key);                        !in_array($key, $ignore_list) ? $auth_rel_model->setAttribute($attr_name, $value) : '';                    }                    $auth_rel_model->save(false);                }            }        }        return true;    }    public function afterTabsave($model, $relation) {        $publisher_model = ProducerAccount::checkPublisher($this->proAcc->Pro_Internal_Code, false);        if (!empty($publisher_model)) {            if (!empty($publisher_model->$relation)) {                $dist_model = $publisher_model->$relation;            } else {                $dist_model = new $model;                $dist_model->Pub_Acc_Id = $publisher_model->Pub_Acc_Id;            }            $ignore_list = Myclass::getPublisherconvertIgnorelist();            foreach ($this->attributes as $key => $value) {                $attr_name = str_replace('Pro_', 'Pub_', $key);                !in_array($key, $ignore_list) ? $dist_model->setAttribute($attr_name, $value) : '';            }            $dist_model->after_save_disable = false;            $dist_model->save(false);        }    }}